# 6. Self-Healing CI/CD Pipeline
cat << 'EOF' > "${WORKFLOW_DIR}/android_build.yml"
name: CogniCore Autonomous Build
on:
  workflow_dispatch:
  push:
    branches: [main]
  repository_dispatch:
    types: [build_apk]

jobs:
  build:
    runs-on: ubuntu-latest
    strategy:
      matrix:
        java-version: [17]
    steps:
      - uses: actions/checkout@v4
      
      - name: Set up JDK 17
        uses: actions/setup-java@v4
        with:
          java-version: 17
          distribution: 'temurin'
          
      - name: Setup Android SDK
        uses: android-actions/setup-android@v3
        
      - name: Gradle Caching
        uses: gradle/actions/setup-gradle@v3
        
      - name: Build with Auto-Repair
        id: build_step
        run: |
          chmod +x ci/self_heal.sh
          # Ensure wrapper exists
          if [ ! -f gradlew ]; then gradle wrapper; fi
          chmod +x gradlew
          
          # Attempt Build
          ./gradlew assembleDebug --no-daemon --stacktrace 2>&1 | tee build.log
        continue-on-error: true

      - name: Engage Self-Healing Protocol
        if: steps.build_step.outcome == 'failure'
        run: |
          echo "[!] Build failed. Engaging Self-Heal Protocol..."
          ./ci/self_heal.sh build.log
          
          # Commit Fixes
          git config --global user.name 'CogniCore Bot'
          git config --global user.email 'bot@cognicore.dev'
          git add .
          git commit -m "Auto-Fix: Applied Self-Healing Patch" || echo "No changes to commit"
          
          # Retry Build
          echo "[*] Retrying build..."
          ./gradlew assembleDebug --no-daemon

      - name: Verify Artifact
        run: |
          chmod +x ci/verify_and_report.sh
          ./ci/verify_and_report.sh

      - name: Upload APK
        uses: actions/upload-artifact@v4
        with:
          name: CogniCore-Sentinel-APK
          path: app/build/outputs/apk/debug/app-debug.apk
          
      - name: Upload Report
        uses: actions/upload-artifact@v4
        with:
          name: Build-Report
          path: build_report.json
EOF

# 7. Helper Scripts
cat << 'EOF' > "${CI_DIR}/self_heal.sh"
#!/bin/bash
LOG=$1
echo "Analyzing $LOG..."

# Fix 1: Missing Wrapper
if grep -q "Task 'wrapper' not found" "$LOG"; then
  echo "Fixing: Missing Wrapper"
  gradle wrapper --gradle-version 8.2.2
fi

# Fix 2: SDK License
if grep -q "license" "$LOG"; then
  echo "Fixing: SDK License"
  mkdir -p "$ANDROID_SDK_ROOT/licenses"
  echo -e "
8933bad161af4178b1185d1a37fbf41ea5269c55" > "$ANDROID_SDK_ROOT/licenses/android-sdk-license"
fi

# Fix 3: Namespace/SDK Version Mismatch (Common in upgrades)
if grep -q "namespace" "$LOG"; then
    echo "Fixing: Gradle Namespace Config"
    sed -i 's/compileSdk 34/compileSdk 33/g' app/build.gradle
fi
EOF
chmod +x "${CI_DIR}/self_heal.sh"

cat << 'EOF' > "${CI_DIR}/verify_and_report.sh"
#!/bin/bash
APK="app/build/outputs/apk/debug/app-debug.apk"
if [ ! -f "$APK" ]; then
  echo '{"status": "failed", "reason": "apk_missing"}' > build_report.json
  exit 1
fi

SIZE=$(wc -c < "$APK")
echo "{"status": "success", "size": $SIZE, "timestamp": "$(date)"}" > build_report.json
EOF
chmod +x "${CI_DIR}/verify_and_report.sh"

# 8. G3P GitOps Automation (Mobile/Termux Optimized)
cat << 'EOF' > "${PROJECT_ROOT}/automate_gitops.sh"
#!/bin/bash
# G3P Full GitOps Automation Instructions
# Target Environment: Termux / Mobile

# 1. Define Target
REPO_URL=${1:-"https://github.com/Genesisfile/CogniCore.git"}
BRANCH="main"
echo "[*] Target: $REPO_URL ($BRANCH)"

# 2. Ensure Files
if [ ! -f gradlew ]; then
    echo "[!] gradlew missing. Attempting bootstrap..."
    if command -v gradle &> /dev/null; then
        gradle wrapper
    else
        echo "[!] Gradle not found locally. CI/CD will auto-bootstrap wrapper."
    fi
fi

# 3. Permissions
chmod +x gradlew 2>/dev/null || true

# 4. Git Init & Remote
if [ ! -d ".git" ]; then
    git init
    git branch -m $BRANCH
    git remote add origin "$REPO_URL"
else
    git remote set-url origin "$REPO_URL"
fi

# 5. Stage
git add gradlew gradlew.bat gradle/wrapper/* build.gradle settings.gradle MOBILE_BUILD_GUIDE.md .github/workflows/android_build.yml app src ci simulations

# 6. Commit
git config --global user.email "bot@cognicore.ai"
git config --global user.name "CogniCore Sentinel"
git commit -m "Add Gradle wrapper, build configs, and project sources for mobile GitOps" || echo "No changes."

# 7. Pull/Rebase
git pull origin $BRANCH --rebase || echo "Remote pull failed (new repo?), continuing..."

# 8. Push
git push -u origin $BRANCH

# 9. Trigger
echo "[*] Triggering Cloud Build..."
echo "# Trigger build $(date)" >> TRIGGER_BUILD.md
git add TRIGGER_BUILD.md
git commit -m "Trigger workflow"
git push origin $BRANCH

echo "[SUCCESS] GitOps Cycle Complete."
EOF
chmod +x "${PROJECT_ROOT}/automate_gitops.sh"

